import { RestApiService } from './../../services/rest-api.service';
import { FormBuilder } from '@angular/forms';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-updateproduct',
  templateUrl: './updateproduct.component.html',
  styleUrls: ['./updateproduct.component.css']
})
export class UpdateproductComponent implements OnInit {
EditFurnitures: any = this.fb.group({
    title: [''],
    description: [''],
    category: [''],
    image: [''],
    status: [''],
    prize: [''],


  })
  updateid:any

  constructor(private fb:FormBuilder,private router:ActivatedRoute,private rest:RestApiService) { }

  ngOnInit(): void {
    this.router.params.subscribe((params)=>{
      this.updateid=params['id']
      this.getDetails(params['id'])

    })
  }

 async getDetails(id:any){
  console.log("get",id);


    const data:any=await this.rest.get(`http://localhost:3000/api/admin/getproduct/${id}`)
    console.log("id details",data.data);
    this.EditFurnitures.patchValue(data.data)


  }


  async editFurnitures(){
   console.log("iddd",this.updateid);
   console.log(this.EditFurnitures.value);



      const data:any=await this.rest.put(`http://localhost:3000/api/admin/updateproduct/${this.updateid}`,this.EditFurnitures.value)
      console.log("final",data);




    console.log(this.EditFurnitures.value)
      //  const data:any=await this.rest.update

  }

}

<div class="container  mt-5">
    <form [formGroup]="EditFurnitures" (ngSubmit)="editFurnitures()" class="w-50 ">
        <div class="header text-center"><h4>Update Furnitures</h4></div>
        <div class="inp-item mt-2">
            <input type="text" name="" id="" class="form-control" placeholder="Title" formControlName="title">
        </div>
        <div class="inp-item mt-2">
            <input type="text" name="" id="" class="form-control" placeholder="Description" formControlName="description">
        </div>
        <div class="inp-item mt-2">
            <select type="text" name="" id="" class="form-control"  formControlName="category">
                <option value="">Select Category-----</option>
                <option value="Sofas">Sofas</option>
                <option value="Bed">Bed</option>
                <option value="Table">Table</option>
                <option value="StudyDesk">Study Desk</option>
                <option value="OfficeChair">Office chair</option>
                <option value="BeanBags">Bean bags</option>
            </select>
        </div>
        <div class="inp-item mt-2">
            <input type="text" name="" id="" class="form-control" placeholder="Image" formControlName="image">
        </div>
        <div class="inp-item mt-2">
            <select type="text" name="" id="" class="form-control"  formControlName="status">
                     <option value="">Select Status Of product ---------</option>
                     <option value="Available">Available</option>
                     <option value="Outofstock">Outofstock</option>
            </select>
        </div>
        <div class="inp-item mt-2">
            <input type="text" name="" id="" class="form-control" placeholder="Prize" formControlName="prize">
        </div>
        <div class="form-btn mt-4 ">
             <button class="form-control" type="submit">Update</button>
        </div>
    </form>
</div>
