import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder } from '@angular/forms';
import { Router } from '@angular/router';
import { RestApiService } from 'src/app/services/rest-api.service';

@Component({
  selector: 'app-add-product',
  templateUrl: './add-product.component.html',
  styleUrls: ['./add-product.component.css']
})
export class AddProductComponent implements OnInit {
  AddFurnitures: any = this.fb.group({
    title: [''],
    description: [''],
    category: [''],
    image: [''],
    status: [''],
    prize: [''],


  })

  constructor(private fb:FormBuilder,private rest:RestApiService,private router:Router) { }

  ngOnInit(): void {
  }

  async addFurnitures(){


    try{
      console.log("adddddddddddd",this.AddFurnitures);

      const data:any=await this.rest.post("http://localhost:3000/api/admin/addProduct",this.AddFurnitures.value)

      if(data['success']){
        this.router.navigateByUrl('admin/products')
      }

    }catch(error){
      console.log(error);

    }


  }




}

<div class="container  mt-5">
    <form [formGroup]="AddFurnitures" (ngSubmit)="addFurnitures()" class="w-50 ">
        <div class="header text-center"><h4>Add Furnitures</h4></div>
        <div class="inp-item mt-2">
            <input type="text" name="" id="" class="form-control" placeholder="Title" formControlName="title">
        </div>
        <div class="inp-item mt-2">
            <input type="text" name="" id="" class="form-control" placeholder="Description" formControlName="description">
        </div>
        <div class="inp-item mt-2">
            <select type="text" name="" id="" class="form-control"  formControlName="category">
                <option value="">Select Category-----</option>
                <option value="Sofas">Sofas</option>
                <option value="Bed">Bed</option>
                <option value="Table">Table</option>
                <option value="StudyDesk">Study Desk</option>
                <option value="OfficeChair">Office chair</option>
                <option value="BeanBags">Bean bags</option>
            </select>
        </div>
        <div class="inp-item mt-2">
            <input type="text" name="" id="" class="form-control" placeholder="Image" formControlName="image">
        </div>
        <div class="inp-item mt-2">
            <select type="text" name="" id="" class="form-control"  formControlName="status">
                     <option value="">Select Status Of product ---------</option>
                     <option value="Available">Available</option>
                     <option value="Outofstock">Outofstock</option>
            </select>
        </div>
        <div class="inp-item mt-2">
            <input type="text" name="" id="" class="form-control" placeholder="Prize" formControlName="prize">
        </div>
        <div class="form-btn mt-4 ">
             <button class="form-control" type="submit">Add</button>
        </div>
    </form>
</div>
